CREATE TABLE `class_abilities` (
  `id` int(11) NOT NULL auto_increment,
  `name` text NOT NULL,
  `type` enum('Ex','Su','Sp') default NULL,
  `mod_string` varchar(20) NOT NULL,
  `description` text,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=78 DEFAULT CHARSET=latin1;
